import React, {useContext} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
// import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import { Link } from 'react-router-dom';
import SavedContext from '../contexts/SavedContext';
import PropsContext from '../contexts/PropsContext';

const useStyles = makeStyles(theme => ({
    root: {
      flexGrow: 1,
    },
    toolbar: {
        justifyContent: 'space-between',
    },
    title: {
      flexGrow: 1,
    },
  }));

  const clearAll = (savedContext, savedKeys, propsContext) => {
    const propsKeys = Object.keys(propsContext.savedProps);
    console.log(savedKeys);
    for (var i = 0; i < savedKeys.length; i++) {
      savedContext.removeFromSaved(savedKeys[i]);
    }
    for (var j = 0; j < propsKeys.length; j++) {
      propsContext.removeFromSavedProps(propsKeys[j]);
    }

  }




const AccountTopBar = (props) => {
    const classes = useStyles();
    const savedContext = useContext(SavedContext);
    const propsContext = useContext(PropsContext);

    return (
        <div className={classes.root}>
          <AppBar position="fixed">
            <Toolbar className={classes.toolbar}>
                <Grid item>
              {/* <Typography 
                variant="h6" 
                className={classes.title}
                component={Route}
                path="/"
                >
                AuthentiCar
              </Typography> */}
              <Button 
                color="inherit"
                component={Link}
                to="/"
              >
                AuthentiCar
              </Button>
              </Grid>
              <Grid item>
                <Button 
                  color="inherit"
                  component={Link}
                  to="/log"
                  >
                  Log Car
                </Button>
              </Grid>
              <Grid item>
                <Button 
                  color="inherit"
                  component={Link}
                  to="/login"
                  onClick={() => {
                    clearAll(savedContext, props.savedKeys, propsContext);

                  }}
                  >
                  Sign Out
                </Button>
              </Grid>
            </Toolbar>
          </AppBar>
        </div>
      );
}

export default AccountTopBar;
