    //         // let object = {
    //         //     carName: stringRes[0],
    //         //     carVin: stringRes[1],
    //         //     carOwner: stringRes[2],
    //         //     carOdometer: stringRes[3]

    //         // }
    //         // objSave.push(object);
    //         let carName = stringRes[0];
    //         let carVin = stringRes[1];
    //         // console.log(stringRes[1]);
    //         // streamContext.addToStream(stringRes[1]);
    //         let carOwner = stringRes[2];
    //         let carOdometer = stringRes[3];
    //         let carImage = '';
    //         carName = carName.toUpperCase();
    //         if (carName === 'HONDA NSX') {
    //             carImage = 'honda_nsx.jpg'

    //         }
    //         if (carVin === search) {
    //             return(<Grid item>
    //             <CarComponent 
    //             carName={carName}
    //             carVin={carVin}
    //             carOwner={carOwner}
    //             carOdometer={carOdometer}
    //             carImage={carImage}
    //             />
    //             </Grid>
    //             );
    //         }

    //     }
}




    // let load = [];
    // console.log(carLogs)
    return (
        <>
        <Search />
        <Grid 
        container 
        spacing={2} 
        justify='center' 
        className={classes.itemGrid}
    >
        {loading()}
        {compareVin()}
        </Grid>



        </>
      );



    }






const Search = (props) => {
    const classes = useStyles();
    const [values, setValues] = React.useState({
        search: ''
      });
    const handleChange = name => event => {
        setValues({ ...values, [name]: event.target.value });
    };
    // const handleKeyPress = (event) => {
    //   if(event.key === 'Enter'){
    //     console.log('enter press here! ');

    //   }
    // };

    const handleSubmit = (event) => {
        console.log(values.search);
        search = values.search;
    }
        return (
            <div className={classes.root}>
          <AppBar position="fixed">
            <Toolbar className={classes.toolbar}>
                <Grid item>
              <Button 
                color="inherit"
                component={Link}
                to="/"
              >
                AuthentiCar
              </Button>
              </Grid>

            <div className={classes.search}>
            <div className={classes.searchIcon}>
              <SearchIcon />
            </div>
            <InputBase
              placeholder="Searchâ€¦"
              classes={{
                root: classes.inputRoot,
                input: classes.inputInput,
              }}
              value={values.search}
              onChange={handleChange('search')}
              onKeyPress={handleSubmit}
              inputProps={{ 'aria-label': 'search' }}
            />
          </div>
              <Grid item>
                <Button 
                  color="inherit"
                  component={Link}
                  to="/account"
                  >
                  Account
                </Button>
              </Grid>
            </Toolbar>
          </AppBar>
        </div>

        );

}








export default SearchContainer;
